import React, { Component,useState } from 'react';
import HouseCard from './HouseCard';
import { Card,Form, Col,Row } from 'react-bootstrap';
import {
    CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle
  } from 'reactstrap';


import {connect} from 'react-redux';
import {getItems,deleteItem} from '../actions/itemActions'
import PropTypes from 'prop-types';

class ShoppingList extends Component{
    state={
        search:''
    }
    static propTypes={
        getItems:PropTypes.func.isRequired,
        item:PropTypes.object.isRequired,
        isAuthenticated:PropTypes.bool
    }

    componentDidMount(id){
        this.props.getItems(id);
    }

    // onDeleteClick=(id)=>{
    //     this.props.deleteItem(id)
    // }
    handleSearch = (e) => {
        e.preventDefault()
        this.setState({search:e.target.value})

    }

    render() {
        const {items}=this.props.item;
        return (
        <div>

                <div style={{backgroundImage:"url(https://cdn.pixabay.com/photo/2017/06/26/17/33/tunisia-2444524_960_720.jpg)",height:'400px',width:'900px',marginTop:"100px", }}>
                <h2> prepare yourself for an adventure!</h2>
                    <div>
                <Form.Control size="lg" type="text" placeholder="Search" width="100px" onInput={this.handleSearch} />
                    </div>
                </div>

        <br/>
        <Row>
            {items.filter(el => el.maison_dhote.toUpperCase().includes(this.state.search.toUpperCase())).map(el =>
            <Col>
             <Card style={{ width: '18rem' }}>
             <CardBody>
                    <CardTitle>{el.maison_dhote}</CardTitle>
                    <CardSubtitle>{el.governorate},{el.sector}</CardSubtitle>
                            </CardBody>
                            <Card.Img variant="top" height="300px" />
                            <CardBody>
                    <CardText>{el.num},{el.email}</CardText>
                            <CardLink href={el.website}>Contact</CardLink>
             </CardBody>
           </Card>
           </Col>
            )}
        </Row>


    </div>        
        );
      }
}



const mapStateToProps=(state)=>({
    item:state.item,
    isAuthenticated:state.auth.isAuthenticated
});

export default connect(mapStateToProps,{getItems,deleteItem})(ShoppingList);
