import React from "react";
import { MDBRow, MDBCol, MDBInput, MDBBtn,Container,MDBContainer,MDBCard } from "mdbreact";
import {connect} from 'react-redux';
import PropTypes from 'prop-types'
import {register} from '../../actions/authActions';
import{clearErrors} from '../../actions/errorActions';

class NewUserRegister extends React.Component {
  state = {
    name: "",
    last_name: "",
    email: "",
    city: "",
    password: ""
  };
      static propTypes={
        isAuthenticated:PropTypes.bool,
        error:PropTypes.object.isRequired,
        register:PropTypes.func.isRequired,
        clearErrors:PropTypes.func.isRequired
    };

    toggle=()=>{
      //clear errors
      this.props.clearErrors();
      this.setState({
          modal:!this.state.modal
      })
  }
  componentDidUpdate(prevProps){
      const {error,isAuthenticated} = this.props;
      if(error!==prevProps.error){
          //check for register error
          if(error.id==='REGISTER_FAIL'){
              this.setState({msg:error.msg.msg})
          } else{
              this.setState({msg:null})
          }
      }
      if(this.state.modal){
          if(isAuthenticated){
              this.toggle();
          }
      }
  }

   ;
   onChange=(e)=>{
       this.setState({[e.target.name]:e.target.value})
   }
   onSubmit=(e)=>{
       e.preventDefault();
      //  const {name,email,password}=this.state;

       //create user object
       const newUser={
           name:this.state.name,
           last_name:this.state.last_name,
           email:this.state.email,
           password:this.state.password,
           city:this.state.city
       }
       this.props.register(newUser);
   };

  render() {
    return (
      
      <MDBContainer>
          <form
          className='needs-validation'
          onSubmit={this.submitHandler}
          noValidate
        >
          <MDBRow>
            <MDBCol md='4'>
              <MDBInput
                icon='user'
                value={this.state.fname}
                name='fname'
                onChange={this.changeHandler}
                type='text'
                id='materialFormRegisterNameEx'
                label='First name'
                required
              >
                <div className='valid-feedback ml-4 pl-3'>Looks good!</div>
              </MDBInput>
            </MDBCol>
            <MDBCol md='4'>
              <MDBInput
                icon='address-card'
                value={this.state.lname}
                name='lname'
                onChange={this.changeHandler}
                type='text'
                id='materialFormRegisterEmailEx2'
                label='Last name'
                required
              >
                <div className='valid-feedback ml-4 pl-3'>Looks good!</div>
              </MDBInput>
            </MDBCol>
            <MDBCol md='4'>
              <MDBInput
                icon='envelope-open'
                value={this.state.email}
                onChange={this.changeHandler}
                type='email'
                id='materialFormRegisterConfirmEx3'
                name='email'
                label='Your Email address'
              >
                <small id='emailHelp' className='form-text text-muted'>
                  We'll never share your email with anyone else.
                </small>
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol md='4'>
              <MDBInput
                icon='city'
                value={this.state.city}
                onChange={this.changeHandler}
                type='text'
                id='materialFormRegisterPasswordEx4'
                name='city'
                label='City'
                required
              >
                <div className='invalid-feedback ml-4 pl-3'>
                  Please provide a valid city.
                </div>
                <div className='valid-feedback ml-4 pl-3'>Looks good!</div>
              </MDBInput>
            </MDBCol>
            <MDBCol md='4'>
                <MDBInput
                    label="Type your password"
                    icon="lock"
                    group
                    type="password"
                    validate
                  >
                <div className='invalid-feedback ml-4 pl-3'>
                  Please provide a valid state.
                </div>
                <div className='valid-feedback ml-4 pl-3'>Looks good!</div>
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBInput
              type='checkbox'
              value='conditions'
              id='materialInvalidCheck'
              required
              label='Agree to terms and conditions'
            >
              <div className='invalid-feedback'>
                You must agree before submitting.
              </div>
            </MDBInput>
          </MDBRow>
          <MDBBtn color='primary' type='submit'>
            Submit Form
          </MDBBtn>
        </form>
        </MDBContainer>
    );
  }
}
const mapStateToProps=state=>({
  isAuthenticated:state.auth.isAuthenticated,
  error:state.error    
})


export default connect(mapStateToProps,{register,clearErrors})(NewUserRegister)
