import React, { Component } from 'react';

import {Container,ListGroup,ListGroupItem,Button} from 'reactstrap';
import {CSSTransition, TransitionGroup} from 'react-transition-group';
import {connect} from 'react-redux';
import {getItems,deleteItem} from '../actions/itemActions'
import PropTypes from 'prop-types';
import {Card} from 'react-bootstrap'

class ShoppingList extends Component{
    static propTypes={
        getItems:PropTypes.func.isRequired,
        item:PropTypes.object.isRequired,
        isAuthenticated:PropTypes.bool
    }

    componentDidMount(id){
        this.props.getItems(id);
    }

    onDeleteClick=(id)=>{
        this.props.deleteItem(id)
    }

    render() {
        const {items}=this.props.item;
        return (
          <div style={{ display: "flex", justifyContent: "space-around" }}>
          <ListGroup>
              <TransitionGroup className="shopping-list">
          {items.map((d) =>(
              <CSSTransition key={d._id} timeout={500} classNames="fade">
                <ListGroupItem>
                {this.props.isAuthenticated?<Button
                    className="remove-btn"
                    color="danger"
                    size="sm"
                    onClick={this.onDeleteClick.bind(this,d._id)}>&times;</Button>:null}

                    {d.maison_dhote}
                    </ListGroupItem>
                </CSSTransition>

          ))}
          </TransitionGroup>
        </ListGroup>

          {items.map((d) =>(
            <Card style={{ width: '18rem' }} >
                    <Card.Img variant="top" height="300px" />
                    <Card.Body>
                        <Card.Title>{d.maison_dhote}</Card.Title>
                    </Card.Body> 
            </Card>))}

            </div>
        );
      }
}



const mapStateToProps=(state)=>({
    item:state.item,
    isAuthenticated:state.auth.isAuthenticated
});

export default connect(mapStateToProps,{getItems,deleteItem})(ShoppingList);
