import { GET_ITEMS, DELETE_ITEM, ADD_ITEM,ITEMS_LOADING } from './types';
import axios from 'axios'
import {tokenConfig} from './authActions';
import {returnErrors} from './errorActions'

export const getItems=()=>dispatch=>{
    dispatch(setItemsLoaoding());
    axios
    .get(('/api/locations'))
    .then(res=>
        dispatch({
            type:GET_ITEMS,
            payload:res.data
        }))
        .catch(err=>dispatch(returnErrors(err.response.data,err.response.status)))
}
export const addItem=(location)=>(dispatch,getState)=>{
    axios.post('/api/locations',location,tokenConfig(getState))
    .then(res=>dispatch({
        type:ADD_ITEM,
        payload:res.data
    }))
    .catch(err=>dispatch(returnErrors(err.response.data,err.response.status)))

};
export const deleteItem=(id)=>(dispatch,getState)=>{
    axios.delete(`/api/locations/${id}`,tokenConfig(getState)).then(res=>
        dispatch({
            type:DELETE_ITEM,
            payload:id
        }))
        .catch(err=>dispatch(returnErrors(err.response.data,err.response.status)))

};

export const setItemsLoaoding=()=>{
    return {
        type:ITEMS_LOADING
    };
};
