import React, { Component,useState } from 'react';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';

import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {getItems,deleteItem} from '../actions/itemActions'
import PropTypes from 'prop-types';

class itemDetails extends Component{
    state={
        search:''
    }
    static propTypes={
        getItems:PropTypes.func.isRequired,
        item:PropTypes.object.isRequired,
        isAuthenticated:PropTypes.bool
    }

    componentDidMount(id){
        this.props.getItems(id);
    }

    // onDeleteClick=(id)=>{
    //     this.props.deleteItem(id)
    // }
    handleSearch = (e) => {
        e.preventDefault()
        this.setState({search:e.target.value})

    }

    render() {
        const {items}=this.props.item;
        console.log(items)
        return (
        <div>
            <Card style={{marginTop:'100px'}}>
              <CardImg top width="100%" src="url(https://a0.muscache.com/im/pictures/be088744-346c-4b1a-91d1-48897e574aac.jpg?im_w=960)"/>
              <CardImg top width="100%" src="/assets/318x180.svg"/>
              <CardImg top width="100%" src="/assets/318x180.svg"/>
              <CardBody>
                <CardTitle>Card title</CardTitle>
                <CardSubtitle>Card subtitle</CardSubtitle>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                <Button>Button</Button>
              </CardBody>
            </Card>
        </div>       
        );
      }
}



const mapStateToProps=(state)=>({
    item:state.item,
    isAuthenticated:state.auth.isAuthenticated
});

export default connect(mapStateToProps,{getItems,deleteItem})(itemDetails);
