import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody,
    FormText
} from 'reactstrap';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {addItem} from '../actions/itemActions';
import PropTypes from 'prop-types';


 class ItemModal extends Component{
     state={
         modal:false,
         maison_dhote:'',
         sector:'',
         num:'',
         email:'',
         website:'',
         governorate:'',
         image:''
     };
     static propTypes={
         isAuthenticated:PropTypes.bool
     }

     toggle=()=>{
         this.setState({
             modal:!this.state.modal
         })
     };
     onChange=(e)=>{
         this.setState({[e.target.maison_dhote]:e.target.value,[e.target.sector]:e.target.value,[e.target.num]:e.target.value,[e.target.email]:e.target.value,[e.target.website]:e.target.value,[e.target.governorate]:e.target.value,[e.target.image]:e.target.value})
     }
     onSubmit=(e)=>{
         e.preventDefault()
         const newLocation={
            
                sector:this.state.sector,
                governorate:this.state.governorate,
                maison_dhote:this.state.maison_dhote,
                num:this.state.num,
                email:this.state.email,
                website:this.state.website,
         }

         this.props.addItem(newLocation)
         this.toggle()
     }

     render (){
         return(
             <div>           
                 <Form  onSubmit ={this.onSubmit}>
                        <FormGroup>
                            <Label for="sector">Sector</Label>
                            <Input type="text" name="sector" id="sector" placeholder="Enter a sector" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="governorate">Governorate</Label>
                            <Input type="text" name="governorate" id="governorate" placeholder="Enter a governorate" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="maison_dhote">Maison d'hote</Label>
                            <Input type="text" name="maison_dhote" id="maison_dhote" placeholder="Enter Your a Name" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="num">Password</Label>
                            <Input type="number" name="num" id="num" placeholder="Enter a Number" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="email" name="email" id="email" placeholder="Enter Your Email" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="website">Website</Label>
                            <Input type="text" name="website" id="website" placeholder="Enter a website" onChange={this.onChange} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="image">Image</Label>
                            <Input type="text" name="image" id="image" placeholder="Enter an image URL" onChange={this.onChange} />
                            </FormGroup>
                        <Button>Submit</Button>
                    </Form>
             </div>
         )
     }

 }

 const mapStateToProps=state=>({
     location:state.location,
     isAuthenticated:state.auth.isAuthenticated
 })

 export default connect(mapStateToProps,{addItem})(ItemModal)
