import React, { Component,useState } from 'react';
import HouseCard from './HouseCard';
import { Form, Col,Row } from 'react-bootstrap';


import {connect} from 'react-redux';
import {getItems,deleteItem} from '../../actions/itemActions'
import PropTypes from 'prop-types';

class ShoppingList extends Component{
    state={
        search:''
    }
    static propTypes={
        getItems:PropTypes.func.isRequired,
        item:PropTypes.object.isRequired,
        isAuthenticated:PropTypes.bool
    }

    componentDidMount(id){
        this.props.getItems(id);
    }

    onDeleteClick=(id)=>{
        this.props.deleteItem(id)
    }
    handleSearch = (e) => {
        e.preventDefault()
        this.setState({search:e.target.value})

    }

    render() {
        const {items}=this.props.item;
        return (

            <div >
        <Row>
            <Col>
                <Form.Control size="lg" type="text" placeholder="Search" width="300px" onInput={this.handleSearch} />
            </Col>
        </Row>
        <div style={{ display: "flex", justifyContent: "space-around" }}>{this.props.isAuthenticated?<Button
                    className="remove-btn"
                    color="danger"
                     size="sm"
                     onClick={this.onDeleteClick.bind(this,d._id)}>&times;</Button>:null}
            {items.filter(el => el.maison_dhote.toUpperCase().includes(this.search.toUpperCase())).map(el => <HouseCard maison_dhote={el.maison_dhote} image={el.image} sector={el.sector} governorate={el.governorate} num={el.num} email={el.email} website={el.website} />)}
        </div>

    </div>        
        );
      }
}



const mapStateToProps=(state)=>({
    item:state.item,
    isAuthenticated:state.auth.isAuthenticated
});

export default connect(mapStateToProps,{getItems,deleteItem})(ShoppingList);
