import React,{setState} from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBContainer, MDBMask, MDBView,MDBBtn, MDBInput } from 'mdbreact';
import { BrowserRouter as Router,Link } from 'react-router-dom';

class Weather extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
      query:'',
      weather:{}
    };
  }
  api = {
    key: "7d4ef5d23648c7c62acd3ccfa1a0a30b",
    base: "https://api.openweathermap.org/data/2.5/"
  }
 
  search = evt => {
    if (evt.key === "Enter") {
      fetch(`${this.api.base}weather?q=${this.state.query}&units=metric&APPID=${this.api.key}`)
        .then(res => res.json())
        .then(result => {
          this.setState({weather:result});
          this.setState({query:''});
          console.log(result);
        });
    }
  }
   dateBuilder = (d) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"]
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`}

  
  

  render() {
    return (
      <div>
        <header>
          
          <MDBView src="https://i.pinimg.com/originals/16/21/a1/1621a1e03793f1c27d93ed9d94c57388.jpg" style={{padding:"10%"}}>
            <MDBMask overlay="indigo-slight" className="flex-center flex-column text-white text-center">
              {/* <h2 style={{fontSize:'70px'}}>Let's get lost in Paradise</h2>
              <h4 style={{fontSize:'40px'}}>Please choose Your identity</h4> */}
              <div style={{display:'flex', flexDirection:'column',justifyContent:"space-around"}}>
                {/* <MDBBtn gradient="purple"><Link to="/newuser">NewUser</Link></MDBBtn>
                <MDBBtn gradient="purple"><Link to="/user">User</Link></MDBBtn>
                <MDBBtn gradient="purple"><Link to="/admin">Admin</Link></MDBBtn> */}
                <MDBInput type="text" placeholder="Search..." onChange={e=>
                  this.setState({query:e.target.value})
                } />
                {(typeof this.state.weather.main!="undefined")?(
                  <div>
              <h4 style={{fontSize:'40px'}}>'{this.state.weather.name},{this.state.weather.sys.country}</h4>
              {this.dateBuilder(new Date())}
              <h4 style={{fontSize:'120px',fontWeight:'900',textShadow:'3px 6px rgba(50,50,70,8.5)',color:'#FFF'}}>{Math.round(this.state.weather.main.temp)}Â°C</h4>
              <h4 style={{fontSize:'40px'}}> {this.state.weather.weather[0].main}</h4>
              </div>
                ):('')}

                {/* <MDBBtn gradient="blue">Event Creator</MDBBtn>


                <MDBBtn gradient="aqua">Host</MDBBtn> */}
              </div>
            </MDBMask>
          </MDBView>
          
        </header>
      </div>
    );
  }
}

export default Weather;
