import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody,
    FormText
} from 'reactstrap';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {addItem} from '../actions/itemActions';
import PropTypes from 'prop-types';


 class ItemModal extends Component{
     state={
         modal:false,
         maison_dhote:'',
         sector:'',
         num:'',
         email:'',
         website:'',
         governorate:'',


     };
     static propTypes={
         isAuthenticated:PropTypes.bool
     }

     toggle=()=>{
         this.setState({
             modal:!this.state.modal
         })
     };
     onChange=(e)=>{
         this.setState({[e.target.maison_dhote]:e.target.value})
     }
     onSubmit=(e)=>{
         e.preventDefault()
         const newLocation={

                sector:this.state.sector,
                governorate:this.state.governorate,
                maison_dhote:this.state.maison_dhote,
                num:this.state.num,
                email:this.state.email,
                website:this.state.website,
         }

         this.props.addItem(newLocation)
         this.toggle()
     }

     render (){
         return(
             <div>
                 {this.props.isAuthenticated?<Link to='/addItem'><Button color="dark" style={{marginBottom:'2rem'}}
                 onClick={this.toggle}>Add Location</Button></Link>: <h4 classmaison_dhote="mb-3 ml-4">Please login to manage Locations</h4>}
                 <Modal isOpen={this.state.modal}
                     toggle={this.toggle}>
                    <ModalHeader toggle={this.toggle}>Add To Locations</ModalHeader>
                    <ModalBody>
                        <Form onSubmit ={this.onSubmit}>
                            <FormGroup>
                                <Label for ="item">Location</Label>
                                <Input type="text"
                                maison_dhote="maison_dhote"
                                id="maison_dhote"
                                placeholder="add maison_dhote"
                                onChange={this.onChange}/>
                                 <Input type="text"
                                sector="sector"
                                id="sector"
                                placeholder="add sector"
                                onChange={this.onChange}/>
                                 <Input type="text"
                                governorate="governorate"
                                id="governorate"
                                placeholder="add governorate"
                                onChange={this.onChange}/>
                                 <Input type="number"
                                num="number"
                                id="number"
                                placeholder="add number"
                                onChange={this.onChange}/>
                                 <Input type="text"
                                email="email"
                                id="email"
                                placeholder="add email"
                                onChange={this.onChange}/>
                                 <Input type="text"
                                website="website"
                                id="website"
                                placeholder="add website"
                                onChange={this.onChange}/>
                                <Button color="dark"
                                style={{marginTop:'2rem'}} block>Add Location</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>

                 </Modal>
             </div>
         )
     }

 }

 const mapStateToProps=state=>({
     location:state.location,
     isAuthenticated:state.auth.isAuthenticated
 })

 export default connect(mapStateToProps,{addItem})(ItemModal)
