import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody,
    FormText
} from 'reactstrap';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {editItem,getItems} from '../actions/itemActions';
import PropTypes from 'prop-types';


 class ItemModal extends Component{
    constructor(props) {
        super(props);
        this.state={
            ID : this.props.match.params.id
        }
        this.onChangeMaison = this.onChangeSector.bind(this);
        this.onChangeSector = this.onChangeSector.bind(this);
        this.onChangeGovernorate = this.onChangeGovernorate.bind(this);
        this.onChangeImage = this.onChangeImage.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangeNum = this.onChangeNum.bind(this);
        this.onChangeWebsite = this.onChangeWebsite.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    
        this.state = {
            maison_dhote:'',
            sector:'',
            num:'',
            email:'',
            website:'',
            governorate:'',
            image:''
        }
      }
     static propTypes={
         isAuthenticated:PropTypes.bool,
         editItem:PropTypes.func.isRequired,
         getItems:PropTypes.func.isRequired
     }
     
     onChangeMaison=(e)=>{
         this.setState({
             maison_dhote:e.target.value
         })
     }
     onChangeSector=(e)=>{
        this.setState({
            sector:e.target.value
        })
    }
    onChangeGovernorate=(e)=>{
        this.setState({
            governorate:e.target.value
        })
    }
    onChangeWebsite=(e)=>{
        this.setState({
            website:e.target.value
        })
    }
    onChangeImage=(e)=>{
        this.setState({
            image:e.target.files[0]
        })
    }
    onChangeEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    onChangeNum=(e)=>{
        this.setState({
            num:e.target.value
        })
    }
     onSubmit=(e)=>{
         e.preventDefault()
         let formdata= new FormData()
         formdata.append('sector',this.state.sector)
         formdata.append('governorate',this.state.governorate)
         formdata.append('maison_dhote',this.state.maison_dhote)
         formdata.append('num',this.state.num)
         formdata.append('email',this.state.email)
         formdata.append('website',this.state.website)
         formdata.append('image',this.state.image)
         const id=this.props.match.params.id

         this.props.editItem(formdata,id)
     }

     render(){
         const {items}=this.props.item
         return (
             <div>
            {items.filter(el => el._id==this.state.ID).map(el =>        
                 <Form onSubmit ={this.onSubmit} style={{marginTop:'100px'}}>
                        <FormGroup>
                            <Label for="sector">Sector</Label>
                            <Input type="text" name="sector" id="sector" placeholder="Enter a sector" value={el.sector} onChange={this.onChangeSector} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="governorate">Governorate</Label>
                            <Input type="text" name="governorate" id="governorate" placeholder="Enter a governorate" onChange={this.onChangeGovernorate} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="maison_dhote">Maison d'hote</Label>
                            <Input type="text" name="maison_dhote" id="maison_dhote" placeholder="Enter Your a Name" onChange={this.onChangeMaison} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="num">Number</Label>
                            <Input type="number" name="num" id="num" placeholder="Enter a Number" onChange={this.onChangeNum} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="email" name="email" id="email" placeholder="Enter Your Email" onChange={this.onChangeEmail} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="website">Website</Label>
                            <Input type="text" name="website" id="website" placeholder="Enter a website" onChange={this.onChangeWebsite} />
                            </FormGroup>
                            <FormGroup>
                            <Label for="image">Image</Label>
                            <Input type="file" name="image" id="image" placeholder="Enter an image URL" onChange={this.onChangeImage} />
                            </FormGroup>
                        <Button>Submit</Button>
                    </Form>)}
                    </div>
         )
     }
    }

    const mapStateToProps=state=>({
      location:state.location,
      item:state.item,
      isAuthenticated:state.auth.isAuthenticated
  })

 export default connect(mapStateToProps,{editItem,getItems})(ItemModal)
