import React from 'react';
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavbarToggler, MDBCollapse, MDBNavItem, MDBNavLink, MDBContainer, MDBMask, MDBView,MDBBtn, MDBInput } from 'mdbreact';
import { BrowserRouter as Router,Link } from 'react-router-dom';

class Weather extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: false,
    };
    this.onClick = this.onClick.bind(this);
  }
  api = {
    key: "7d4ef5d23648c7c62acd3ccfa1a0a30b",
    base: "https://api.openweathermap.org/data/2.5/"
  }

  onClick() {
    this.setState({
      collapse: !this.state.collapse,
    });
  }

  render() {
    return (
      <div>
        <header>
          
          <MDBView src="https://i.pinimg.com/originals/16/21/a1/1621a1e03793f1c27d93ed9d94c57388.jpg" style={{padding:"10%"}}>
            <MDBMask overlay="indigo-slight" className="flex-center flex-column text-white text-center">
              {/* <h2 style={{fontSize:'70px'}}>Let's get lost in Paradise</h2>
              <h4 style={{fontSize:'40px'}}>Please choose Your identity</h4> */}
              <div style={{display:'flex', flexDirection:'row',justifyContent:"space-around"}}>
                {/* <MDBBtn gradient="purple"><Link to="/newuser">NewUser</Link></MDBBtn>
                <MDBBtn gradient="purple"><Link to="/user">User</Link></MDBBtn>
                <MDBBtn gradient="purple"><Link to="/admin">Admin</Link></MDBBtn> */}
                <div>
                <MDBInput type="text" placeholder="Search..."/>
                </div>
                
                
              <h4 style={{fontSize:'40px'}}>New YOrk City,US</h4>
                

                {/* <MDBBtn gradient="blue">Event Creator</MDBBtn>


                <MDBBtn gradient="aqua">Host</MDBBtn> */}
              </div>
            </MDBMask>
          </MDBView>
          
        </header>
      </div>
    );
  }
}

export default Weather;
