import React, { Component,useState } from 'react';
import HouseCard from './HouseCard';
import { Form, Col,Row } from 'react-bootstrap';
import {
    Card, CardImg, CardText, CardBody, CardLink,
    CardTitle, CardSubtitle
  } from 'reactstrap';


import {connect} from 'react-redux';
import {getItems,deleteItem} from '../../actions/itemActions'
import PropTypes from 'prop-types';

class ShoppingList extends Component{
    state={
        search:''
    }
    static propTypes={
        getItems:PropTypes.func.isRequired,
        item:PropTypes.object.isRequired,
        isAuthenticated:PropTypes.bool
    }

    componentDidMount(id){
        this.props.getItems(id);
    }

    onDeleteClick=(id)=>{
        this.props.deleteItem(id)
    }
    handleSearch = (e) => {
        e.preventDefault()
        this.setState({search:e.target.value})

    }

    render() {
        const {items}=this.props.item;
        return (

            <div >
        <Row>
            <Col>
                <Form.Control size="lg" type="text" placeholder="Search" width="300px" onInput={this.handleSearch} />
            </Col>
        </Row>
        <div style={{ display: "flex", justifyContent: "space-around" }}>
            {items.filter(el => el.maison_dhote.toUpperCase().includes(this.search.toUpperCase())).map(el =>
             <Card>
             <CardBody>
                    <CardTitle>{el.maison_dhote}</CardTitle>
                    <CardSubtitle>{el.governorate},{el.sector}</CardSubtitle>
                            </CardBody>
                            <img width="100%" src="/assets/318x180.svg" alt="Card image cap" />
                            <CardBody>
                    <CardText>{el.num},{el.email}</CardText>
                            <CardLink href={el.website}>Contact</CardLink>
             </CardBody>
           </Card>

            )}
        </div>

    </div>        
        );
      }
}



const mapStateToProps=(state)=>({
    item:state.item,
    isAuthenticated:state.auth.isAuthenticated
});

export default connect(mapStateToProps,{getItems,deleteItem})(ShoppingList);
