import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    Form,
    FormGroup,
    Label,
    Input,
    ModalBody
} from 'reactstrap';


import {connect} from 'react-redux';
import {addItem} from '../actions/itemActions';
import PropTypes from 'prop-types';

 class ItemModal extends Component{
     state={
         modal:false,
         maison_dhote:''
     };
     static propTypes={
         isAuthenticated:PropTypes.bool
     }

     toggle=()=>{
         this.setState({
             modal:!this.state.modal
         })
     };
     onChange=(e)=>{
         this.setState({[e.target.maison_dhote]:e.target.value})
     }
     onSubmit=(e)=>{
         e.preventDefault()
         const newItem={
            maison_dhote:this.state.maison_dhote
         }

         this.props.addItem(newItem)
         this.toggle()
     }

     render (){
         return(
             <div>
                 {this.props.isAuthenticated?<Button color="dark" style={{marginBottom:'2rem'}}
                 onClick={this.toggle}>Add Item</Button>: <h4 classmaison_dhote="mb-3 ml-4">Please login to manage Items</h4>}
                 <Modal isOpen={this.state.modal}
                     toggle={this.toggle}>
                    <ModalHeader toggle={this.toggle}>Add To Shopping List</ModalHeader>
                    <ModalBody>
                        <Form onSubmit ={this.onSubmit}>
                            <FormGroup>
                                <Label for ="item">Location</Label>
                                <Input type="text"
                                maison_dhote="maison_dhote"
                                id="item"
                                placeholder="add shopping item"
                                onChange={this.onChange}/>
                                <Button color="dark"
                                style={{marginTop:'2rem'}} block>Add Location</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>

                 </Modal>
             </div>
         )
     }

 }

 const mapStateToProps=state=>({
     item:state.item,
     isAuthenticated:state.auth.isAuthenticated
 })

 export default connect(mapStateToProps,{addItem})(ItemModal)
